module.exports = {
  get_config_data: function(){
    var fs   = require('fs');
    var config_yaml = require('config-yaml/config-yaml').get_config_yaml();
    try {
      var config = fs.readFileSync('./' + config_yaml.blueprint.layout_path);
      return config;
    } catch (e) {
      return new Error(e.message);
    }
  }
}
